import numpy as np
A = np.array([[1, 2, 3], [2, 3, 4], [4, 5, 6]])
new_row = np.array([7, 8, 9])
print(new_row)
A = np.insert(A, 2, new_row, axis=1)
A = np.hstack((A, [[1], [2], [3]]))
print(A)
print("Smallest of each row: ")
smallest_of_each_row = np.min(A, axis=0)
print(smallest_of_each_row)
print("Smallest of each column: ")
smallest_of_each_column = np.min(A, axis=1)
print(smallest_of_each_column)
print("Sum of elements in each row: ")
row_sum = np.sum(A, axis=1)
print(row_sum)
print("Each rows sorted: ")
sorted_rows = np.sort(A, axis=1)
print(sorted_rows)
print("Sorted columns: ")
sorted_columns = np.sort(A, axis=0)
print(sorted_columns)
print(A.shape)
